import React, { useCallback, useRef, useState } from 'react'
import { Button, Spin, Upload } from 'antd'
import { CheckOutlined, PlusOutlined, DownloadOutlined } from '@ant-design/icons'
import Cropper from './components/Cropper'
import download from 'downloadjs'

import './App.css'
import Header from './components/Header'

const { Dragger } = Upload

const App = () => {
  const [cropState, setCropState] = useState()
  const [croppedImageUrl, setCroppedImageUrl] = useState('');
  const [img, setImg] = useState()
  const cropperRef = useRef()
  const hiddenAnchorRef = useRef()
  //const previewCanvasRef = useRef<HTMLCanvasElement>(null)
  const blobUrlRef = useRef()
  const onDragStop = useCallback((s) => setCropState(s), [])
  const onChange = useCallback((s) => setCropState(s), [])

  const doSomething = async () => {
    console.log('CropState', cropState)
    try {
      const res = await cropperRef.current.done({
        preview: true,
        filterCvParams: {
          thMeanCorrection: 13,
          thMode: window.cv.ADAPTIVE_THRESH_GAUSSIAN_C
        }
      });
      console.log('Cropped and filtered image', res)
      const blob = await fetch(res.preview).then((r) => r.blob())
      setCroppedImageUrl(res.preview);
    } catch (e) {
      console.log('error', e)
    }
  }

  // function onDownloadCropClick() {
  //   if (!cropperRef.current) {
  //     throw new Error('Crop canvas does not exist');
  //   }

  //   cropperRef.current.toBlob(function (blob) {
  //     if (!blob) {
  //       throw new Error('Failed to create blob');
  //     }
  //     if (blobUrlRef.current) {
  //       URL.revokeObjectURL(blobUrlRef.current);
  //     }
  //     blobUrlRef.current = URL.createObjectURL(blob);
  //     hiddenAnchorRef.current.href = blobUrlRef.current;
  //     hiddenAnchorRef.current.click();
  //   });
  // }

  const onDownloadCropClick = () => {
    if (!cropperRef.current) {
      throw new Error('Cropper canvas does not exist');
    }

    const canvas = cropperRef.current;
    const context = canvas.getContext('2d');

    if (!context) {
      throw new Error('Failed to get 2D rendering context');
    }

    canvas.toBlob((blob) => {
      if (!blob) {
        throw new Error('Failed to create blob');
      }

      const downloadUrl = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.download = 'cropped_image.jpg';
      link.click();

      // Clean up the URL object after the download
      URL.revokeObjectURL(downloadUrl);
    });
  };

  const onImgSelection = async (e) => {
    if (e.fileList && e.fileList.length > 0) {
      // it can also be a http or base64 string for example
      setImg(e.fileList[0].originFileObj)
    }
  }

  const draggerProps = {
    name: 'file',
    multiple: false,
    onChange: onImgSelection
  }

  return (
    <div className='root-container'>
      <Header />
      <div className='content-container'>
        {cropState && (
          <div className='buttons-container'>
            <Button onClick={doSomething} icon={<CheckOutlined />}>
              Done
            </Button>
            <Button
              onClick={() => {
                cropperRef.current.backToCrop()
              }}
            >
              Back
            </Button>
            <Button
              onClick={() => {
                setImg(undefined)
                setCropState()
              }}
            >
              Reset
            </Button>
            <Button onClick={onDownloadCropClick}>Download Crop</Button>
            {/* <a
              ref={hiddenAnchorRef}
              download
              style={{
                position: 'absolute',
                top: '-200vh',
                visibility: 'hidden',
              }}
            >
              Hidden download
            </a> */}
          </div>
        )}
        <Cropper
          openCvPath='./opencv/opencv.js'
          ref={cropperRef}
          image={img}
          onChange={onChange}
          onDragStop={onDragStop}
          maxWidth={window.innerWidth - 10}
        />
        {cropState?.loading && <Spin />}
        {!img && (
          <Dragger {...draggerProps}>
            <p>
              <PlusOutlined />
            </p>
            <p>Upload</p>
          </Dragger>
        )}
      </div>
    </div>
  )
}

export default App
